package com.example.demo.view;

import android.content.Context;
import android.graphics.Canvas;
import android.graphics.Color;
import android.graphics.Paint;
import android.graphics.Rect;
import android.util.AttributeSet;
import android.view.MotionEvent;
import android.view.View;
import com.example.common.log.ALog;

/**
 * 学习用的自定义View
 * 展示View的测量、布局、绘制和触摸事件处理流程
 */
public class LearningView extends View {

    private static final String TAG = "LearningView";
    private Paint backgroundPaint;
    private Paint textPaint;
    private Paint borderPaint;
    private String text;
    private Rect textBounds = new Rect();
    private boolean isPressed = false;

    public LearningView(Context context) {
        super(context);
        init();
    }

    public LearningView(Context context, AttributeSet attrs) {
        super(context, attrs);
        init();
    }

    public LearningView(Context context, AttributeSet attrs, int defStyleAttr) {
        super(context, attrs, defStyleAttr);
        init();
    }

    private void init() {
        ALog.d(TAG, "🏗️ init() - View初始化");

        // 初始化画笔
        backgroundPaint = new Paint();
        backgroundPaint.setColor(Color.parseColor("#FFEB3B"));
        backgroundPaint.setStyle(Paint.Style.FILL);

        textPaint = new Paint();
        textPaint.setColor(Color.BLACK);
        textPaint.setTextSize(48f);
        textPaint.setAntiAlias(true);
        textPaint.setTextAlign(Paint.Align.CENTER);

        borderPaint = new Paint();
        borderPaint.setColor(Color.parseColor("#FF9800"));
        borderPaint.setStyle(Paint.Style.STROKE);
        borderPaint.setStrokeWidth(6f);

        text = "学习View";

        // 设置可点击，这样才能接收触摸事件
        setClickable(true);

        ALog.d(TAG, "🏗️ View初始化完成");
    }

    public void setText(String text) {
        this.text = text;
        // 文本改变后需要重新测量和绘制
        requestLayout(); // 请求重新测量和布局
        invalidate();    // 请求重新绘制
    }

    // ==================== 测量阶段 ====================

    @Override
    protected void onMeasure(int widthMeasureSpec, int heightMeasureSpec) {
        ALog.d(TAG, "📏 onMeasure() 开始 - View测量阶段");

        // 解析父容器传来的测量规格
        int widthMode = MeasureSpec.getMode(widthMeasureSpec);
        int widthSize = MeasureSpec.getSize(widthMeasureSpec);
        int heightMode = MeasureSpec.getMode(heightMeasureSpec);
        int heightSize = MeasureSpec.getSize(heightMeasureSpec);

        ALog.d(TAG, "📏 父容器给的宽度规格: " + getModeString(widthMode) + " size=" + widthSize);
        ALog.d(TAG, "📏 父容器给的高度规格: " + getModeString(heightMode) + " size=" + heightSize);

        // 测量文本尺寸
        textPaint.getTextBounds(text, 0, text.length(), textBounds);
        int textWidth = textBounds.width();
        int textHeight = textBounds.height();

        ALog.d(TAG, "📏 文本尺寸: " + textWidth + "x" + textHeight);

        // 计算所需的最小尺寸（文本尺寸 + padding）
        int minWidth = textWidth + getPaddingLeft() + getPaddingRight() + 40; // 额外40px边距
        int minHeight = textHeight + getPaddingTop() + getPaddingBottom() + 40; // 额外40px边距

        ALog.d(TAG, "📏 View最小尺寸需求: " + minWidth + "x" + minHeight);

        // 根据测量规格确定最终尺寸
        int finalWidth = resolveSize(minWidth, widthMeasureSpec);
        int finalHeight = resolveSize(minHeight, heightMeasureSpec);

        ALog.d(TAG, "📏 View最终测量尺寸: " + finalWidth + "x" + finalHeight);

        // 设置测量尺寸
        setMeasuredDimension(finalWidth, finalHeight);

        ALog.d(TAG, "📏 onMeasure() 结束");
    }

    // ==================== 布局阶段 ====================

    @Override
    protected void onLayout(boolean changed, int left, int top, int right, int bottom) {
        ALog.d(TAG, "📐 onLayout() 开始 - View布局阶段");
        ALog.d(TAG, "📐 View位置: left=" + left + ", top=" + top + ", right=" + right + ", bottom=" + bottom);
        ALog.d(TAG, "📐 View尺寸: " + (right - left) + "x" + (bottom - top));
        ALog.d(TAG, "📐 布局是否改变: " + changed);

        super.onLayout(changed, left, top, right, bottom);

        ALog.d(TAG, "📐 onLayout() 结束");
    }

    @Override
    protected void onSizeChanged(int w, int h, int oldw, int oldh) {
        ALog.d(TAG, "📐 onSizeChanged() - 尺寸改变");
        ALog.d(TAG, "📐 新尺寸: " + w + "x" + h);
        ALog.d(TAG, "📐 旧尺寸: " + oldw + "x" + oldh);

        super.onSizeChanged(w, h, oldw, oldh);
    }

    // ==================== 绘制阶段 ====================

    @Override
    protected void onDraw(Canvas canvas) {
        ALog.d(TAG, "🎨 onDraw() 开始 - View绘制阶段");

        super.onDraw(canvas);

        int width = getWidth();
        int height = getHeight();

        ALog.d(TAG, "🎨 绘制区域尺寸: " + width + "x" + height);

        // 根据按压状态改变背景色
        if (isPressed) {
            backgroundPaint.setColor(Color.parseColor("#FFC107")); // 按压时的颜色
        } else {
            backgroundPaint.setColor(Color.parseColor("#FFEB3B")); // 正常颜色
        }

        // 绘制背景
        canvas.drawRect(0, 0, width, height, backgroundPaint);

        // 绘制边框
        canvas.drawRect(0, 0, width, height, borderPaint);

        // 绘制文本（居中）
        float textX = width / 2f;
        float textY = height / 2f - (textPaint.descent() + textPaint.ascent()) / 2f;

        canvas.drawText(text, textX, textY, textPaint);

        ALog.d(TAG, "🎨 View绘制完成");
        ALog.d(TAG, "🎨 onDraw() 结束");
    }

    @Override
    public void draw(Canvas canvas) {
        ALog.d(TAG, "🎨 draw() 开始 - 总绘制方法");
        super.draw(canvas);
        ALog.d(TAG, "🎨 draw() 结束");
    }

    // ==================== 触摸事件处理 ====================

    @Override
    public boolean dispatchTouchEvent(MotionEvent event) {
        String action = getActionString(event.getAction());
        ALog.d(TAG, "👆 dispatchTouchEvent() - " + action + " 坐标:(" + event.getX() + ", " + event.getY() + ")");

        boolean result = super.dispatchTouchEvent(event);

        ALog.d(TAG, "👆 dispatchTouchEvent() 返回: " + result);
        return result;
    }

    @Override
    public boolean onTouchEvent(MotionEvent event) {
        String action = getActionString(event.getAction());
        ALog.d(TAG, "✋ onTouchEvent() - " + action + " View处理触摸事件");

        switch (event.getAction()) {
            case MotionEvent.ACTION_DOWN:
                ALog.d(TAG, "✋ 按下事件 - 设置按压状态");
                isPressed = true;
                invalidate(); // 重新绘制以显示按压效果
                return true; // 消费事件

            case MotionEvent.ACTION_UP:
                ALog.d(TAG, "✋ 抬起事件 - 取消按压状态");
                isPressed = false;
                invalidate(); // 重新绘制以取消按压效果

                // 执行点击操作
                performClick();
                return true;

            case MotionEvent.ACTION_CANCEL:
                ALog.d(TAG, "✋ 取消事件 - 取消按压状态");
                isPressed = false;
                invalidate();
                return true;

            case MotionEvent.ACTION_MOVE:
                // 检查是否还在View范围内
                float x = event.getX();
                float y = event.getY();
                boolean inBounds = x >= 0 && x < getWidth() && y >= 0 && y < getHeight();

                if (isPressed != inBounds) {
                    isPressed = inBounds;
                    invalidate();
                    ALog.d(TAG, "✋ 移动事件 - 按压状态: " + isPressed);
                }
                return true;
        }

        ALog.d(TAG, "✋ onTouchEvent() 返回: false");
        return false;
    }

    @Override
    public boolean performClick() {
        ALog.d(TAG, "🎯 performClick() - 执行点击操作");

        // 调用父类方法以支持点击监听器
        boolean result = super.performClick();

        // 这里可以添加自定义的点击逻辑
        ALog.d(TAG, "🎯 点击了 " + text);

        return result;
    }

    // ==================== 辅助方法 ====================

    private String getModeString(int mode) {
        switch (mode) {
            case MeasureSpec.EXACTLY:
                return "EXACTLY(精确值)";
            case MeasureSpec.AT_MOST:
                return "AT_MOST(最大值)";
            case MeasureSpec.UNSPECIFIED:
                return "UNSPECIFIED(未指定)";
            default:
                return "UNKNOWN";
        }
    }

    private String getActionString(int action) {
        switch (action) {
            case MotionEvent.ACTION_DOWN:
                return "ACTION_DOWN(按下)";
            case MotionEvent.ACTION_MOVE:
                return "ACTION_MOVE(移动)";
            case MotionEvent.ACTION_UP:
                return "ACTION_UP(抬起)";
            case MotionEvent.ACTION_CANCEL:
                return "ACTION_CANCEL(取消)";
            default:
                return "ACTION_" + action;
        }
    }
}

