         * 浮点数范围 m == (bb - aa)
         */
    }

    public static void transform() {
        int a = 0; // 整数范围最小值
        int b = 100; // 整数范围最大值
        float aa = 1.0f; // 浮点数范围最小值
        float bb = 5.0f; // 浮点数范围最大值
        float xx = 0.5f; // 步长

        // 计算浮点数值的个数
        int k = (int) ((bb - aa) / xx);

        // 映射整数到浮点数
        for (int n = 0; n <= 100; n++) {
            float mappedValue = aa + ((float) n / 100) * (bb - aa);
            if (mappedValue % xx == 0) {
                System.out.println("Integer value: " + n + " -> Mapped float value: " + mappedValue);
            }
        }
    }
