import android.view.ViewGroup;
import android.widget.Button;
import android.widget.LinearLayout;

import com.example.common.base.BaseFragment;
import com.example.common.log.ALog;
import com.example.common.routers.Router;
import com.example.common.routers.RouterPath;
import com.example.common.utils.UIUtils;
import com.example.demo.R;
import com.example.demo.databinding.FragmentViewBinding;
import com.therouter.router.Route;

@Route(path = RouterPath.VIEW, description = "View学习页面")
public class ViewFragment extends BaseFragment<FragmentViewBinding> {

    private Handler handler = new Handler(Looper.getMainLooper());
    private boolean isLearningDemoVisible = false;

    public ViewFragment() {
        super(R.layout.fragment_view);
    }

    @Override
    protected void initData() {
        super.initData();
    }

    @Override
    protected void initView() {
        super.initView();

        // 跳转到原有的CustomViewFragment
        binding.buttonCustom.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View view) {
                Router.openFragment(RouterPath.CUSTOM_VIEW);
            }
        });

        // 学习View流程Demo按钮
        binding.buttonLearning.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View view) {
                toggleLearningDemo();
            }
        });
    }

    /**
     * 切换学习Demo的显示/隐藏
     */
    private void toggleLearningDemo() {
        if (isLearningDemoVisible) {
            hideLearningDemo();
        } else {
            showLearningDemo();
        }
    }

    /**
     * 显示学习Demo
     */
    private void showLearningDemo() {
        ALog.d("ViewFragment", "开始显示学习Demo");

        // 显示容器
        binding.learningContainer.setVisibility(View.VISIBLE);

        // 清空之前的内容
        binding.learningContainer.removeAllViews();

        // 添加标题
        android.widget.TextView titleView = new android.widget.TextView(getContext());
        titleView.setText("View学习Demo - 查看日志观察流程");
        titleView.setTextSize(16f);
        titleView.setTypeface(null, android.graphics.Typeface.BOLD);
        LinearLayout.LayoutParams titleParams = new LinearLayout.LayoutParams(
            ViewGroup.LayoutParams.WRAP_CONTENT, ViewGroup.LayoutParams.WRAP_CONTENT);
        titleParams.bottomMargin = UIUtils.dp2pxInt(8);
        titleView.setLayoutParams(titleParams);
        binding.learningContainer.addView(titleView);

        // 创建学习用的ViewGroup
        LearningViewGroup learningViewGroup = new LearningViewGroup(getContext());
        LinearLayout.LayoutParams viewGroupParams = new LinearLayout.LayoutParams(
            ViewGroup.LayoutParams.MATCH_PARENT, UIUtils.dp2pxInt(200));
        viewGroupParams.topMargin = UIUtils.dp2pxInt(8);
        viewGroupParams.bottomMargin = UIUtils.dp2pxInt(8);
        learningViewGroup.setLayoutParams(viewGroupParams);
        learningViewGroup.setPadding(UIUtils.dp2pxInt(16), UIUtils.dp2pxInt(16),
                                   UIUtils.dp2pxInt(16), UIUtils.dp2pxInt(16));

        // 创建学习用的子View
        LearningView learningView1 = new LearningView(getContext());
        learningView1.setText("View1");

        LearningView learningView2 = new LearningView(getContext());
        learningView2.setText("View2");

        LearningView learningView3 = new LearningView(getContext());
        learningView3.setText("View3");

        // 添加子View到ViewGroup
        learningViewGroup.addView(learningView1);
        learningViewGroup.addView(learningView2);
        learningViewGroup.addView(learningView3);

        // 添加ViewGroup到容器
        binding.learningContainer.addView(learningViewGroup);

        // 添加控制按钮
        LinearLayout buttonContainer = new LinearLayout(getContext());
        buttonContainer.setOrientation(LinearLayout.HORIZONTAL);
        LinearLayout.LayoutParams buttonContainerParams = new LinearLayout.LayoutParams(
            ViewGroup.LayoutParams.MATCH_PARENT, ViewGroup.LayoutParams.WRAP_CONTENT);
        buttonContainerParams.topMargin = UIUtils.dp2pxInt(8);
        buttonContainer.setLayoutParams(buttonContainerParams);

        // 重新测量按钮
        Button remeasureButton = new Button(getContext());
        remeasureButton.setText("重新测量");
        remeasureButton.setOnClickListener(v -> {
            ALog.d("ViewFragment", "用户点击重新测量按钮");
            learningViewGroup.requestLayout();
        });

        // 重新绘制按钮
        Button redrawButton = new Button(getContext());
        redrawButton.setText("重新绘制");
        redrawButton.setOnClickListener(v -> {
            ALog.d("ViewFragment", "用户点击重新绘制按钮");
            learningViewGroup.invalidate();
        });

        // 改变文本按钮
        Button changeTextButton = new Button(getContext());
        changeTextButton.setText("改变文本");
        changeTextButton.setOnClickListener(v -> {
            ALog.d("ViewFragment", "用户点击改变文本按钮");
            learningView1.setText("新文本" + System.currentTimeMillis() % 1000);
        });

        buttonContainer.addView(remeasureButton);
        buttonContainer.addView(redrawButton);
        buttonContainer.addView(changeTextButton);

        binding.learningContainer.addView(buttonContainer);

        // 添加说明文本
        android.widget.TextView instructionView = new android.widget.TextView(getContext());
        instructionView.setText("操作说明：\n" +
                "• 点击上面的View观察触摸事件流程\n" +
                "• 点击按钮观察测量、布局、绘制流程\n" +
                "• 查看Logcat中的详细日志输出\n" +
                "• 日志标签：LearningViewGroup, LearningView");
        instructionView.setTextSize(12f);
        instructionView.setTextColor(Color.GRAY);
        LinearLayout.LayoutParams instructionParams = new LinearLayout.LayoutParams(
            ViewGroup.LayoutParams.MATCH_PARENT, ViewGroup.LayoutParams.WRAP_CONTENT);
        instructionParams.topMargin = UIUtils.dp2pxInt(16);
        instructionView.setLayoutParams(instructionParams);
        binding.learningContainer.addView(instructionView);

        isLearningDemoVisible = true;
        binding.buttonLearning.setText("隐藏学习Demo");

        ALog.d("ViewFragment", "学习Demo显示完成");
    }

    /**
     * 隐藏学习Demo
     */
    private void hideLearningDemo() {
        ALog.d("ViewFragment", "隐藏学习Demo");

        binding.learningContainer.setVisibility(View.GONE);
        binding.learningContainer.removeAllViews();

        isLearningDemoVisible = false;
        binding.buttonLearning.setText("学习View流程Demo");
    }
}

