package com.example.demo.view;

import android.content.Context;
import android.graphics.Canvas;
import android.graphics.Color;
import android.graphics.Paint;
import android.util.AttributeSet;
import android.view.MotionEvent;
import android.view.View;
import android.view.ViewGroup;
import com.example.common.log.ALog;

/**
 * 学习用的自定义ViewGroup
 * 展示View的测量、布局、绘制和触摸事件分发流程
 */
public class LearningViewGroup extends ViewGroup {

    private static final String TAG = "LearningViewGroup";
    private Paint backgroundPaint;
    private Paint borderPaint;

    public LearningViewGroup(Context context) {
        super(context);
        init();
    }

    public LearningViewGroup(Context context, AttributeSet attrs) {
        super(context, attrs);
        init();
    }

    public LearningViewGroup(Context context, AttributeSet attrs, int defStyleAttr) {
        super(context, attrs, defStyleAttr);
        init();
    }

    private void init() {
        ALog.d(TAG, "init() - ViewGroup初始化");

        // 初始化画笔
        backgroundPaint = new Paint();
        backgroundPaint.setColor(Color.parseColor("#E3F2FD"));
        backgroundPaint.setStyle(Paint.Style.FILL);

        borderPaint = new Paint();
        borderPaint.setColor(Color.parseColor("#2196F3"));
        borderPaint.setStyle(Paint.Style.STROKE);
        borderPaint.setStrokeWidth(4f);

        // 设置可点击，这样才能接收触摸事件
        setClickable(true);

        // 设置背景色，方便观察
        setBackgroundColor(Color.parseColor("#F5F5F5"));
    }

    // ==================== 测量阶段 ====================

    @Override
    protected void onMeasure(int widthMeasureSpec, int heightMeasureSpec) {
        ALog.d(TAG, "onMeasure() 开始 - ViewGroup测量阶段");

        // 解析父容器传来的测量规格
        int widthMode = MeasureSpec.getMode(widthMeasureSpec);
        int widthSize = MeasureSpec.getSize(widthMeasureSpec);
        int heightMode = MeasureSpec.getMode(heightMeasureSpec);
        int heightSize = MeasureSpec.getSize(heightMeasureSpec);

        ALog.d(TAG, "父容器给的宽度规格: " + getModeString(widthMode) + " size=" + widthSize);
        ALog.d(TAG, "父容器给的高度规格: " + getModeString(heightMode) + " size=" + heightSize);

        // 测量所有子View
        int childCount = getChildCount();
        ALog.d(TAG, "开始测量 " + childCount + " 个子View");

        int totalChildWidth = 0;
        int maxChildHeight = 0;

        for (int i = 0; i < childCount; i++) {
            View child = getChildAt(i);
            if (child.getVisibility() != GONE) {
                ALog.d(TAG, "测量第 " + i + " 个子View: " + child.getClass().getSimpleName());

                // 为子View创建测量规格
                int childWidthMeasureSpec = MeasureSpec.makeMeasureSpec(0, MeasureSpec.UNSPECIFIED);
                int childHeightMeasureSpec = MeasureSpec.makeMeasureSpec(0, MeasureSpec.UNSPECIFIED);

                // 测量子View
                child.measure(childWidthMeasureSpec, childHeightMeasureSpec);

                int childWidth = child.getMeasuredWidth();
                int childHeight = child.getMeasuredHeight();

                ALog.d(TAG, "第 " + i + " 个子View测量结果: " + childWidth + "x" + childHeight);

                totalChildWidth += childWidth;
                maxChildHeight = Math.max(maxChildHeight, childHeight);
            }
        }

        // 计算自己的尺寸
        int finalWidth = resolveSize(totalChildWidth + getPaddingLeft() + getPaddingRight(), widthMeasureSpec);
        int finalHeight = resolveSize(maxChildHeight + getPaddingTop() + getPaddingBottom(), heightMeasureSpec);

        ALog.d(TAG, "ViewGroup最终测量尺寸: " + finalWidth + "x" + finalHeight);

        // 设置测量尺寸
        setMeasuredDimension(finalWidth, finalHeight);

        ALog.d(TAG, "onMeasure() 结束");
    }

    // ==================== 布局阶段 ====================

    @Override
    protected void onLayout(boolean changed, int l, int t, int r, int b) {
        ALog.d(TAG, "onLayout() 开始 - ViewGroup布局阶段");
        ALog.d(TAG, "ViewGroup位置: left=" + l + ", top=" + t + ", right=" + r + ", bottom=" + b);
        ALog.d(TAG, "ViewGroup尺寸: " + (r - l) + "x" + (b - t));
        ALog.d(TAG, "布局是否改变: " + changed);

        int childCount = getChildCount();
        int currentLeft = getPaddingLeft();
        int currentTop = getPaddingTop();

        ALog.d(TAG, "开始布局 " + childCount + " 个子View");

        for (int i = 0; i < childCount; i++) {
            View child = getChildAt(i);
            if (child.getVisibility() != GONE) {
                int childWidth = child.getMeasuredWidth();
                int childHeight = child.getMeasuredHeight();

                int childLeft = currentLeft;
                int childTop = currentTop;
                int childRight = childLeft + childWidth;
                int childBottom = childTop + childHeight;

                ALog.d(TAG, "布局第 " + i + " 个子View: " + child.getClass().getSimpleName());
                ALog.d(TAG, "子View位置: left=" + childLeft + ", top=" + childTop + 
                       ", right=" + childRight + ", bottom=" + childBottom);

                // 布局子View
                child.layout(childLeft, childTop, childRight, childBottom);

                // 水平排列，下一个子View的left位置
                currentLeft += childWidth + 20; // 添加20px间距
            }
        }

        ALog.d(TAG, "onLayout() 结束");
    }

    // ==================== 绘制阶段 ====================

    @Override
    protected void onDraw(Canvas canvas) {
        ALog.d(TAG, "onDraw() 开始 - ViewGroup绘制阶段");

        super.onDraw(canvas);

        // 绘制背景
        canvas.drawRect(0, 0, getWidth(), getHeight(), backgroundPaint);

        // 绘制边框
        canvas.drawRect(0, 0, getWidth(), getHeight(), borderPaint);

        ALog.d(TAG, "ViewGroup绘制完成，尺寸: " + getWidth() + "x" + getHeight());
        ALog.d(TAG, "onDraw() 结束");
    }

    @Override
    protected void dispatchDraw(Canvas canvas) {
        ALog.d(TAG, "dispatchDraw() 开始 - 分发绘制给子View");
        super.dispatchDraw(canvas);
        ALog.d(TAG, "dispatchDraw() 结束");
    }

    @Override
    public void draw(Canvas canvas) {
        ALog.d(TAG, "draw() 开始 - 总绘制方法");
        super.draw(canvas);
        ALog.d(TAG, "draw() 结束");
    }

    // ==================== 触摸事件分发 ====================

    @Override
    public boolean dispatchTouchEvent(MotionEvent ev) {
        String action = getActionString(ev.getAction());
        ALog.d(TAG, "dispatchTouchEvent() - " + action + " 坐标:(" + ev.getX() + ", " + ev.getY() + ")");

        boolean result = super.dispatchTouchEvent(ev);

        ALog.d(TAG, "dispatchTouchEvent() 返回: " + result);
        return result;
    }

    @Override
    public boolean onInterceptTouchEvent(MotionEvent ev) {
        String action = getActionString(ev.getAction());
        ALog.d(TAG, "onInterceptTouchEvent() - " + action + " 是否拦截事件?");

        // 这里可以决定是否拦截事件，不让子View处理
        boolean intercept = false; // 默认不拦截

        ALog.d(TAG, "onInterceptTouchEvent() 返回: " + intercept);
        return intercept;
    }

    @Override
    public boolean onTouchEvent(MotionEvent event) {
        String action = getActionString(event.getAction());
        ALog.d(TAG, "onTouchEvent() - " + action + " ViewGroup处理触摸事件");

        // 简单处理：消费所有事件
        boolean handled = true;

        ALog.d(TAG, "onTouchEvent() 返回: " + handled);
        return handled;
    }

    // ==================== 辅助方法 ====================

    private String getModeString(int mode) {
        switch (mode) {
            case MeasureSpec.EXACTLY:
                return "EXACTLY(精确值)";
            case MeasureSpec.AT_MOST:
                return "AT_MOST(最大值)";
            case MeasureSpec.UNSPECIFIED:
                return "UNSPECIFIED(未指定)";
            default:
                return "UNKNOWN";
        }
    }

    private String getActionString(int action) {
        switch (action) {
            case MotionEvent.ACTION_DOWN:
                return "ACTION_DOWN(按下)";
            case MotionEvent.ACTION_MOVE:
                return "ACTION_MOVE(移动)";
            case MotionEvent.ACTION_UP:
                return "ACTION_UP(抬起)";
            case MotionEvent.ACTION_CANCEL:
                return "ACTION_CANCEL(取消)";
            default:
                return "ACTION_" + action;
        }
    }
}

