import android.content.pm.ActivityInfo;
import android.widget.EditText;
import android.widget.Toast;

import com.example.common.base.BaseActivity;
import com.example.common.routers.RouterPath;
import com.example.common.routers.Router;
import com.example.demo.databinding.ActivityMainBinding;

public class MainActivity extends BaseActivity<ActivityMainBinding> {

    public MainActivity() {
        super(R.layout.activity_main);
    }

    @Override
    protected void initData() {
        super.initData();
//        Router.switchFragment(RouterPath.TEST_CAMERA);
    }

    @Override
    protected void initView() {
        super.initView();

        binding.btnView.setOnClickListener(v -> {
            Router.openFragment(RouterPath.VIEW);
        });

        binding.btnAPI.setOnClickListener(v -> {
            Router.openFragment(RouterPath.API);
        });

        binding.btnStudy.setOnClickListener(v -> {
            Router.openFragment(RouterPath.STUDY_MAIN);
        });

        // 设置横屏按钮
        binding.btnLandscape.setOnClickListener(v -> {
            setRequestedOrientation(ActivityInfo.SCREEN_ORIENTATION_LANDSCAPE);
            Toast.makeText(this, "已设置为横屏模式", Toast.LENGTH_SHORT).show();
        });

        // 设置竖屏按钮
        binding.btnPortrait.setOnClickListener(v -> {
            setRequestedOrientation(ActivityInfo.SCREEN_ORIENTATION_PORTRAIT);
            Toast.makeText(this, "已设置为竖屏模式", Toast.LENGTH_SHORT).show();
        });

        // 获取当前屏幕方向按钮
        binding.btnGetOrientation.setOnClickListener(v -> {
            int orientation = getRequestedOrientation();
            String orientationStr;

            switch (orientation) {
                case ActivityInfo.SCREEN_ORIENTATION_LANDSCAPE:
                    orientationStr = "横屏模式";
                    break;
                case ActivityInfo.SCREEN_ORIENTATION_PORTRAIT:
                    orientationStr = "竖屏模式";
                    break;
                case ActivityInfo.SCREEN_ORIENTATION_UNSPECIFIED:
                    orientationStr = "未指定方向";
                    break;
                default:
                    orientationStr = "其他方向模式: " + orientation;
                    break;
            }

            Toast.makeText(this, "当前屏幕方向: " + orientationStr, Toast.LENGTH_SHORT).show();
        });
    }
}"}
