package com.example.demo;

import android.os.Bundle;
import android.util.Log;
import android.widget.ArrayAdapter;
import android.widget.Spinner;

import androidx.appcompat.app.AppCompatActivity;

public class MainActivity extends AppCompatActivity {

    private static final String TAG = "MainActivity";
    private CustomEditText customEditText;
    private Spinner fontWeightSpinner;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        customEditText = findViewById(R.id.customEditText);
        fontWeightSpinner = findViewById(R.id.fontWeightSpinner);

        String[] fontWeights = {"100", "300", "400", "600", "800", "900"};
        ArrayAdapter<String> adapter = new ArrayAdapter<>(this, android.R.layout.simple_spinner_item, fontWeights);
        adapter.setDropDownViewResource(android.R.layout.simple_spinner_dropdown_item);
        fontWeightSpinner.setAdapter(adapter);

        // 设置初始文本和字体粗细
        customEditText.setText("测试文本");
        customEditText.setFontWeight(400); // 设置默认字体粗细为400

        fontWeightSpinner.setOnItemSelectedListener(new android.widget.AdapterView.OnItemSelectedListener() {
            @Override
            public void onItemSelected(android.widget.AdapterView<?> parent, android.view.View view, int position, long id) {
                int weight = Integer.parseInt(fontWeights[position]);
                Log.d(TAG, "Setting font weight: " + weight);
                customEditText.setFontWeight(weight);
            }

            @Override
            public void onNothingSelected(android.widget.AdapterView<?> parent) {
                // Do nothing
            }
        });
    }
}

