package com.example.demo;

import android.content.Context;
import android.util.AttributeSet;
import android.util.Log;
import android.view.inputmethod.EditorInfo;
import android.view.inputmethod.InputConnection;
import android.view.inputmethod.InputConnectionWrapper;

import androidx.appcompat.widget.AppCompatEditText;
import androidx.core.widget.TextViewCompat;

public class CustomEditText extends AppCompatEditText {
    private static final String TAG = "CustomEditText";

    public CustomEditText(Context context) {
        super(context);
    }

    public CustomEditText(Context context, AttributeSet attrs) {
        super(context, attrs);
    }

    public CustomEditText(Context context, AttributeSet attrs, int defStyleAttr) {
        super(context, attrs, defStyleAttr);
    }

    @Override
    public InputConnection onCreateInputConnection(EditorInfo outAttrs) {
        return new CustomInputConnection(super.onCreateInputConnection(outAttrs), true);
    }

    private class CustomInputConnection extends InputConnectionWrapper {
        public CustomInputConnection(InputConnection target, boolean mutable) {
            super(target, mutable);
        }

        @Override
        public boolean setComposingText(CharSequence text, int newCursorPosition) {
            Log.d(TAG, "keyboardcompositionupdate: " + text);
            return super.setComposingText(text, newCursorPosition);
        }

        @Override
        public boolean commitText(CharSequence text, int newCursorPosition) {
            Log.d(TAG, "input: " + text);
            return super.commitText(text, newCursorPosition);
        }
    }

    public void setFontWeight(int weight) {
        Log.d(TAG, "Setting font weight: " + weight);
        int style;
        switch (weight) {
            case 100:
                style = R.style.CustomTextAppearance_Thin;
                break;
            case 300:
                style = R.style.CustomTextAppearance_Light;
                break;
            case 400:
                style = R.style.CustomTextAppearance_Regular;
                break;
            case 600:
                style = R.style.CustomTextAppearance_SemiBold;
                break;
            case 800:
                style = R.style.CustomTextAppearance_ExtraBold;
                break;
            case 900:
                style = R.style.CustomTextAppearance_Black;
                break;
            default:
                style = R.style.CustomTextAppearance_Regular;
        }
        TextViewCompat.setTextAppearance(this, style);
        invalidate();
        requestLayout();
    }
}

