        TextView hint = new TextView(getContext());
        hint.setText("ğŸ” è¯•è¯•çœ‹ï¼šåœ¨çº¢è‰²åŒºåŸŸæ°´å¹³æ»‘åŠ¨ï¼Œä¼šå‘ç°æ»‘åŠ¨ä½“éªŒå¾ˆå·®");
        hint.setTextSize(12);
