    }

    /**
     * æœ‰å†²çªçš„ScrollView - ä¸åšä»»ä½•ç‰¹æ®Šå¤„ç†
     */
    private static class ConflictScrollView extends ScrollView {
        private static final String TAG = "ScrollConflictDemo.ConflictScrollView";

        public ConflictScrollView(Context context) {
            super(context);
        }

        @Override
        public boolean onInterceptTouchEvent(MotionEvent ev) {
            ALog.dd(TAG, "onInterceptTouchEvent: action=" + getActionName(ev.getAction()) + 
                    ", x=" + ev.getX() + ", y=" + ev.getY());

            // é»˜è®¤è¡Œä¸ºï¼šScrollViewä¼šæ‹¦æˆªæ‰€æœ‰æ»‘åŠ¨äº‹ä»¶
            boolean result = super.onInterceptTouchEvent(ev);
            ALog.dd(TAG, "âš ï¸ ScrollViewæ‹¦æˆªå†³å®š: " + result + " (è¿™å°±æ˜¯å†²çªçš„æ ¹æº!)");

            if (result) {
                ALog.dd(TAG, "ğŸš« ScrollViewæ‹¦æˆªäº†äº‹ä»¶ï¼Œå­Viewå°†æ”¶åˆ°ACTION_CANCEL");
            } else {
                ALog.dd(TAG, "âœ… ScrollViewä¸æ‹¦æˆªï¼Œäº‹ä»¶ç»§ç»­ä¼ é€’ç»™å­View");
            }

            return result;
        }

        @Override
        public boolean onTouchEvent(MotionEvent event) {
            ALog.dd(TAG, "onTouchEvent: action=" + getActionName(event.getAction()) + 
                    ", x=" + event.getX() + ", y=" + event.getY());

            boolean result = super.onTouchEvent(event);
            ALog.dd(TAG, "onTouchEvent è¿”å›: " + result);
            return result;
        }

        private String getActionName(int action) {
            switch (action) {
                case MotionEvent.ACTION_DOWN: return "DOWN";
                case MotionEvent.ACTION_MOVE: return "MOVE";
                case MotionEvent.ACTION_UP: return "UP";
                case MotionEvent.ACTION_CANCEL: return "CANCEL";
                default: return "OTHER(" + action + ")";
            }
        }
    }

    /**
     * æ°´å¹³æ»‘åŠ¨çš„å­View
     */
    private static class HorizontalScrollChild extends View {
        private static final String TAG = "ScrollConflictDemo.HorizontalScrollChild";

        private float mLastX;
        private float mLastY;
        private float mTranslateX = 0;
        private final float MAX_TRANSLATE = 200;

        public HorizontalScrollChild(Context context) {
            super(context);
            setBackgroundColor(Color.parseColor("#FFCDD2"));
        }

        @Override
        public boolean onTouchEvent(MotionEvent event) {
            float x = event.getX();
            float y = event.getY();

            ALog.dd(TAG, "onTouchEvent: action=" + getActionName(event.getAction()) + 
                    ", x=" + x + ", y=" + y);

            switch (event.getAction()) {
                case MotionEvent.ACTION_DOWN:
                    mLastX = x;
                    mLastY = y;
                    ALog.dd(TAG, "DOWNäº‹ä»¶ - è®°å½•åˆå§‹ä½ç½®: (" + mLastX + ", " + mLastY + ")");
                    return true;

                case MotionEvent.ACTION_MOVE:
                    float dx = x - mLastX;
                    float dy = y - mLastY;

                    ALog.dd(TAG, "MOVEäº‹ä»¶ - dx=" + dx + ", dy=" + dy + 
                            ", å½“å‰translateX=" + mTranslateX);

                    // å°è¯•æ°´å¹³ç§»åŠ¨
                    float newTranslateX = mTranslateX + dx;
                    if (newTranslateX >= -MAX_TRANSLATE && newTranslateX <= MAX_TRANSLATE) {
                        mTranslateX = newTranslateX;
                        setTranslationX(mTranslateX);
