                    } else {
                        ALog.dd(TAG, "âš ï¸ æ°´å¹³ç§»åŠ¨å—é™ï¼Œä¿æŒåœ¨: " + mTranslateX);
                    }

                    mLastX = x;
                    mLastY = y;
                    break;

                case MotionEvent.ACTION_UP:
                    ALog.dd(TAG, "ğŸŠ UPäº‹ä»¶ - æ»‘åŠ¨æ­£å¸¸ç»“æŸï¼Œæœ€ç»ˆä½ç½®: " + mTranslateX);
                    ALog.dd(TAG, "âœ… å®Œç¾ï¼æ•´ä¸ªæ»‘åŠ¨è¿‡ç¨‹æ²¡æœ‰è¢«æ‰“æ–­");
                    break;

                case MotionEvent.ACTION_CANCEL:
                    ALog.dd(TAG, "âŒ CANCELäº‹ä»¶ - è¿™ä¸åº”è¯¥å‘ç”Ÿåœ¨è§£å†³æ–¹æ¡ˆä¸­ï¼");
                    ALog.dd(TAG, "å¦‚æœçœ‹åˆ°è¿™æ¡æ—¥å¿—ï¼Œè¯´æ˜è§£å†³æ–¹æ¡ˆæœ‰é—®é¢˜");
                    break;
            }

            return true;
        }

        private String getActionName(int action) {
            switch (action) {
                case MotionEvent.ACTION_DOWN: return "DOWN";
                case MotionEvent.ACTION_MOVE: return "MOVE";
                case MotionEvent.ACTION_UP: return "UP";
                case MotionEvent.ACTION_CANCEL: return "CANCEL";
                default: return "OTHER(" + action + ")";
            }
        }
    }
}

