@Override
public boolean onInterceptTouchEvent(MotionEvent ev) {
    boolean intercept = false;
    float x = ev.getX();
    float y = ev.getY();

    ALog.dd(TAG, "onInterceptTouchEvent: action=" + getActionName(ev.getAction()) +
            ", x=" + x + ", y=" + y);

    switch (ev.getAction()) {
        case MotionEvent.ACTION_DOWN:
            // DOWNäº‹ä»¶ä¸æ‹¦æˆªï¼Œè®©å­Viewæœ‰æœºä¼šå¤„ç†
            intercept = false;
            mIsHorizontalScroll = false;
            mLastX = x;
            mLastY = y;
            ALog.dd(TAG, "DOWNäº‹ä»¶ - ä¸æ‹¦æˆªï¼Œè®°å½•åˆå§‹ä½ç½®: (" + mLastX + ", " + mLastY + ")");

            // è°ƒç”¨çˆ¶ç±»çš„onInterceptTouchEventï¼Œç¡®ä¿ScrollViewæ­£å¸¸å·¥ä½œ
            super.onInterceptTouchEvent(ev);
            break;

        case MotionEvent.ACTION_MOVE:
            float dx = x - mLastX;
            float dy = y - mLastY;

            ALog.dd(TAG, "MOVEäº‹ä»¶ - dx=" + dx + ", dy=" + dy);
            ALog.dd(TAG, "ğŸ§  æ™ºèƒ½åˆ¤æ–­: |dx|=" + Math.abs(dx) + ", |dy|=" + Math.abs(dy));

