package com.example.demo.view;

import android.graphics.Color;
import android.widget.LinearLayout;
import android.widget.TextView;

import com.example.common.base.BaseFragment;
import com.example.common.log.ALog;
import com.example.common.routers.RouterPath;
import com.example.demo.R;
import com.example.demo.databinding.FragmentViewNestedEventBinding;
import com.therouter.router.Route;

@Route(path = RouterPath.VIEW_NESTED_EVENT, description = "滑动冲突演示")
public class ViewNestedEventFragment extends BaseFragment<FragmentViewNestedEventBinding> {
    private static final String TAG = "ViewNestedEventFragment";

    private ScrollConflictDemo mConflictDemo;
    private ScrollConflictSolvedDemo mSolvedDemo;

    public ViewNestedEventFragment() {
        super(R.layout.fragment_view_nested_event);
    }

    @Override
    protected void initView() {
        super.initView();

        ALog.dd(TAG, "ViewNestedEventFragment initView开始");

        // 创建标题
        TextView titleView = new TextView(getContext());
        titleView.setText("📱 滑动冲突演示与解决方案");
        titleView.setTextSize(24);
        titleView.setTextColor(Color.parseColor("#1A1A1A"));
        titleView.setPadding(30, 30, 30, 20);
        titleView.setTypeface(null, android.graphics.Typeface.BOLD);
        binding.main.addView(titleView);

        // 添加说明文本
        TextView descView = new TextView(getContext());
        descView.setText("🎯 学习目标：\n" +
                "• 理解滑动冲突产生的原因\n" +
                "• 掌握外部拦截法解决滑动冲突\n" +
                "• 学会判断滑动方向和距离\n" +
                "• 观察事件拦截的详细过程\n\n" +
                "📋 演示场景：\n" +
                "垂直滑动的ScrollView + 水平滑动的子View\n" +
                "这是最常见的滑动冲突场景");
        descView.setTextSize(16);
        descView.setTextColor(Color.parseColor("#333333"));
        descView.setPadding(30, 0, 30, 30);
        descView.setLineSpacing(8, 1.0f);
        binding.main.addView(descView);

        // 场景1：有滑动冲突的演示
        TextView conflictTitle = new TextView(getContext());
        conflictTitle.setText("❌ 场景1：存在滑动冲突");
        conflictTitle.setTextSize(20);
        conflictTitle.setTextColor(Color.parseColor("#D32F2F"));
        conflictTitle.setPadding(30, 20, 30, 15);
        conflictTitle.setTypeface(null, android.graphics.Typeface.BOLD);
        binding.main.addView(conflictTitle);

        TextView conflictDesc = new TextView(getContext());
        conflictDesc.setText("🔍 冲突产生原因：\n" +
                "• ScrollView默认会拦截所有滑动事件（包括水平滑动）\n" +
                "• 子View想要处理水平滑动，但被父容器抢先拦截\n\n" +
                "😞 冲突现象：\n" +
                "• 子View收到ACTION_CANCEL，无法完成滑动\n" +
                "• 用户体验：滑动不跟手，响应异常\n" +
                "• 日志显示：父容器拦截 → 子View被取消");
        conflictDesc.setTextSize(14);
        conflictDesc.setTextColor(Color.parseColor("#666666"));
        conflictDesc.setPadding(30, 0, 30, 15);
        conflictDesc.setLineSpacing(6, 1.0f);
        binding.main.addView(conflictDesc);

        // 添加有冲突的演示
        mConflictDemo = ScrollConflictDemo.createDemo(getContext());
        LinearLayout.LayoutParams conflictParams = new LinearLayout.LayoutParams(
            LinearLayout.LayoutParams.MATCH_PARENT, 600  // 增加高度以适应更多内容
        );
        conflictParams.setMargins(25, 10, 25, 20);
        binding.main.addView(mConflictDemo, conflictParams);

        // 添加冲突事件流程说明
        TextView conflictFlow = new TextView(getContext());
        conflictFlow.setText("📋 冲突事件流程：\n" +
                "1. 用户在红色区域水平滑动 👆\n" +
                "2. ScrollView.onInterceptTouchEvent() 返回 true ⚠️\n" +
                "3. 子View收到 ACTION_CANCEL 💥\n" +
                "4. ScrollView处理事件，但这是水平滑动！😵\n" +
                "5. 结果：滑动体验异常，子View无法响应");
        conflictFlow.setTextSize(13);
        conflictFlow.setTextColor(Color.parseColor("#8B0000"));
        conflictFlow.setPadding(30, 15, 30, 0);
        conflictFlow.setBackgroundColor(Color.parseColor("#FFF0F0"));
        conflictFlow.setLineSpacing(4, 1.0f);
        LinearLayout.LayoutParams flowParams = new LinearLayout.LayoutParams(
            LinearLayout.LayoutParams.MATCH_PARENT,
            LinearLayout.LayoutParams.WRAP_CONTENT
        );
        flowParams.setMargins(25, 10, 25, 20);
        binding.main.addView(conflictFlow, flowParams);

        // 场景2：解决滑动冲突的演示
        TextView solvedTitle = new TextView(getContext());
        solvedTitle.setText("✅ 场景2：解决滑动冲突");
        solvedTitle.setTextSize(20);
        solvedTitle.setTextColor(Color.parseColor("#388E3C"));
        solvedTitle.setPadding(30, 30, 30, 15);
        solvedTitle.setTypeface(null, android.graphics.Typeface.BOLD);
        binding.main.addView(solvedTitle);

        TextView solvedDesc = new TextView(getContext());
        solvedDesc.setText("🎉 解决方案：外部拦截法\n" +
                "• 智能判断：根据滑动方向决定谁处理事件\n" +
                "• 水平滑动：ScrollView不拦截，子View正常响应\n" +
                "• 垂直滑动：ScrollView拦截，自己处理滚动\n\n" +
                "✨ 解决效果：\n" +
                "• 子View不再收到ACTION_CANCEL\n" +
                "• 滑动体验流畅自然，各司其职");
        solvedDesc.setTextSize(14);
        solvedDesc.setTextColor(Color.parseColor("#666666"));
        solvedDesc.setPadding(30, 0, 30, 15);
        solvedDesc.setLineSpacing(6, 1.0f);
        binding.main.addView(solvedDesc);

        // 添加解决冲突的演示
        mSolvedDemo = ScrollConflictSolvedDemo.createDemo(getContext());
        LinearLayout.LayoutParams solvedParams = new LinearLayout.LayoutParams(
            LinearLayout.LayoutParams.MATCH_PARENT, 400
        );
        solvedParams.setMargins(25, 10, 25, 20);
        binding.main.addView(mSolvedDemo, solvedParams);

        // 添加解决方案事件流程说明
        TextView solvedFlow = new TextView(getContext());
        solvedFlow.setText("📋 解决方案事件流程：\n" +
                "1. 用户在绿色区域水平滑动 👆\n" +
                "2. SmartScrollView.onInterceptTouchEvent() 智能判断 🧠\n" +
                "   • 计算 dx 和 dy 的大小关系\n" +
                "   • Math.abs(dx) > Math.abs(dy) → 水平滑动\n" +
                "3. 返回 false，不拦截事件 ✅\n" +
                "4. 子View正常收到 MOVE 和 UP 事件 🎉\n" +
                "5. 结果：子View流畅滑动，用户体验完美！");
        solvedFlow.setTextSize(13);
        solvedFlow.setTextColor(Color.parseColor("#2E7D32"));
        solvedFlow.setPadding(30, 15, 30, 0);
        solvedFlow.setBackgroundColor(Color.parseColor("#F1F8E9"));
        solvedFlow.setLineSpacing(4, 1.0f);
        LinearLayout.LayoutParams solvedFlowParams = new LinearLayout.LayoutParams(
            LinearLayout.LayoutParams.MATCH_PARENT,
            LinearLayout.LayoutParams.WRAP_CONTENT
        );
        solvedFlowParams.setMargins(25, 10, 25, 20);
        binding.main.addView(solvedFlow, solvedFlowParams);

        // 添加技术要点说明
        TextView techInfo = new TextView(getContext());
        techInfo.setText("🔧 技术要点：\n\n" +
                "外部拦截法核心逻辑：\n" +
                "• 在父容器的onInterceptTouchEvent()中判断\n" +
                "• DOWN事件：不拦截，让子View有机会处理\n" +
                "• MOVE事件：根据滑动方向决定是否拦截\n" +
                "• UP事件：不拦截，让事件正常结束\n\n" +
                "判断滑动方向：\n" +
                "• 计算dx = x - mLastX, dy = y - mLastY\n" +
                "• 如果Math.abs(dx) > Math.abs(dy)：水平滑动\n" +
                "• 如果Math.abs(dy) > Math.abs(dx)：垂直滑动");
        techInfo.setTextSize(14);
        techInfo.setTextColor(Color.parseColor("#2C2C2C"));
        techInfo.setPadding(25, 20, 25, 20);
        techInfo.setBackgroundColor(Color.parseColor("#F5F5F5"));
        techInfo.setLineSpacing(6, 1.0f);
        LinearLayout.LayoutParams techParams = new LinearLayout.LayoutParams(
            LinearLayout.LayoutParams.MATCH_PARENT,
            LinearLayout.LayoutParams.WRAP_CONTENT
        );
        techParams.setMargins(25, 25, 25, 20);
        binding.main.addView(techInfo, techParams);

        // 添加日志提示
        TextView logTip = new TextView(getContext());
        logTip.setText("💡 调试技巧：使用 adb logcat | grep \"ScrollConflict\" 查看详细的滑动冲突处理日志");
        logTip.setTextSize(14);
        logTip.setTextColor(Color.parseColor("#0066CC"));
        logTip.setPadding(30, 20, 30, 20);
        logTip.setTypeface(null, android.graphics.Typeface.ITALIC);
        binding.main.addView(logTip);

        ALog.dd(TAG, "ViewNestedEventFragment initView完成");
    }

    @Override
    protected void initData() {
        super.initData();
        ALog.dd(TAG, "ViewNestedEventFragment initData");
    }

    @Override
    public void onResume() {
        super.onResume();
        ALog.dd(TAG, "ViewNestedEventFragment onResume");
    }
}

