package com.example.demo.view;

import android.content.Context;
import android.graphics.Color;
import android.util.AttributeSet;
import android.view.MotionEvent;
import android.view.View;
import android.widget.LinearLayout;
import android.widget.ScrollView;
import android.widget.TextView;

import com.example.common.log.ALog;

/**
 * 滑动冲突解决方案演示 - 使用外部拦截法
 * 场景：垂直滑动的ScrollView + 水平滑动的子View
 */
public class ScrollConflictSolvedDemo extends LinearLayout {
    private static final String TAG = "ScrollConflictSolvedDemo";

    public static ScrollConflictSolvedDemo createDemo(Context context) {
        return new ScrollConflictSolvedDemo(context);
    }

    public ScrollConflictSolvedDemo(Context context) {
        super(context);
        init();
    }

    public ScrollConflictSolvedDemo(Context context, AttributeSet attrs) {
        super(context, attrs);
        init();
    }

    private void init() {
        setOrientation(VERTICAL);
        setBackgroundColor(Color.parseColor("#E8F5E8"));
        setPadding(20, 20, 20, 20);

        ALog.dd(TAG, "ScrollConflictSolvedDemo 初始化");

        // 创建标题
        TextView title = new TextView(getContext());
        title.setText("✅ 解决冲突的滑动容器");
        title.setTextSize(16);
        title.setTextColor(Color.parseColor("#388E3C"));
        title.setPadding(0, 0, 0, 15);
        title.setTypeface(null, android.graphics.Typeface.BOLD);
        addView(title);

        // 创建智能的ScrollView
        SmartScrollView scrollView = new SmartScrollView(getContext());
        LinearLayout.LayoutParams scrollParams = new LinearLayout.LayoutParams(
            LayoutParams.MATCH_PARENT, 250  // 调整高度只显示约3个内容项
        );
        scrollView.setLayoutParams(scrollParams);
        scrollView.setBackgroundColor(Color.parseColor("#E1F5FE"));

        // 在ScrollView中添加内容
        LinearLayout scrollContent = new LinearLayout(getContext());
        scrollContent.setOrientation(VERTICAL);
        scrollContent.setPadding(15, 15, 15, 15);

        // 添加一些垂直内容
        for (int i = 0; i < 5; i++) {
            TextView textView = new TextView(getContext());
            textView.setText("垂直滑动内容 " + (i + 1) + " - 试试垂直滑动这个智能ScrollView");
            textView.setTextSize(14);
            textView.setPadding(0, 15, 0, 15);
            textView.setTextColor(Color.parseColor("#0277BD"));
            scrollContent.addView(textView);
        }

        // 添加水平滑动的子View
        HorizontalScrollChild horizontalChild = new HorizontalScrollChild(getContext());
        LinearLayout.LayoutParams childParams = new LinearLayout.LayoutParams(
            LayoutParams.MATCH_PARENT, 120
        );
        childParams.setMargins(0, 10, 0, 10);
        scrollContent.addView(horizontalChild, childParams);

        // 继续添加垂直内容
        for (int i = 5; i < 15; i++) {
            TextView textView = new TextView(getContext());
            textView.setText("垂直滑动内容 " + (i + 1) + " - 智能判断让滑动更流畅");
            textView.setTextSize(14);
            textView.setPadding(0, 15, 0, 15);
            textView.setTextColor(Color.parseColor("#0277BD"));
            scrollContent.addView(textView);
        }

        scrollView.addView(scrollContent);
        addView(scrollView);

        // 添加说明
        TextView hint = new TextView(getContext());
        hint.setText("🎉 试试看：在绿色区域水平滑动，体验流畅的滑动效果");
        hint.setTextSize(12);
        hint.setTextColor(Color.parseColor("#666666"));
        hint.setPadding(0, 10, 0, 0);
        addView(hint);
    }

    /**
     * 智能的ScrollView - 使用外部拦截法解决滑动冲突
     */
    private static class SmartScrollView extends ScrollView {
        private static final String TAG = "ScrollConflictSolvedDemo.SmartScrollView";

        private float mLastX;
        private float mLastY;
        private boolean mIsHorizontalScroll = false;

        public SmartScrollView(Context context) {
            super(context);
        }

        @Override
        public boolean onInterceptTouchEvent(MotionEvent ev) {
            boolean intercept = false;
            float x = ev.getX();
            float y = ev.getY();

            ALog.dd(TAG, "onInterceptTouchEvent: action=" + getActionName(ev.getAction()) +
                    ", x=" + x + ", y=" + y);

            switch (ev.getAction()) {
                case MotionEvent.ACTION_DOWN:
                    // DOWN事件不拦截，让子View有机会处理
                    intercept = false;
                    mIsHorizontalScroll = false;
                    mLastX = x;
                    mLastY = y;
                    ALog.dd(TAG, "DOWN事件 - 不拦截，记录初始位置: (" + mLastX + ", " + mLastY + ")");
                    break;

                case MotionEvent.ACTION_MOVE:
                    float dx = x - mLastX;
                    float dy = y - mLastY;

                    ALog.dd(TAG, "MOVE事件 - dx=" + dx + ", dy=" + dy);
                    ALog.dd(TAG, "🧠 智能判断: |dx|=" + Math.abs(dx) + ", |dy|=" + Math.abs(dy));

                    // 判断滑动方向
                    if (Math.abs(dx) > Math.abs(dy)) {
                        // 水平滑动，不拦截，让子View处理
                        intercept = false;
                        mIsHorizontalScroll = true;
                        ALog.dd(TAG, "✅ 检测到水平滑动 - 不拦截，让子View处理");
                        ALog.dd(TAG, "🎯 解决方案生效：子View将正常响应水平滑动");
                    } else {
                        // 垂直滑动，拦截事件让ScrollView处理
                        intercept = true;
                        mIsHorizontalScroll = false;
                        ALog.dd(TAG, "⬆️ 检测到垂直滑动 - 拦截事件，ScrollView处理");
                        ALog.dd(TAG, "📜 ScrollView正常处理垂直滚动");
                    }
                    break;

                case MotionEvent.ACTION_UP:
                case MotionEvent.ACTION_CANCEL:
                    // UP和CANCEL事件不拦截
                    intercept = false;
                    ALog.dd(TAG, getActionName(ev.getAction()) + "事件 - 不拦截");
                    break;
            }

            ALog.dd(TAG, "onInterceptTouchEvent 返回: " + intercept);
            return intercept;
        }

        @Override
        public boolean onTouchEvent(MotionEvent event) {
            ALog.dd(TAG, "onTouchEvent: action=" + getActionName(event.getAction()) +
                    ", x=" + event.getX() + ", y=" + event.getY());

            boolean result = super.onTouchEvent(event);
            ALog.dd(TAG, "onTouchEvent 返回: " + result);
            return result;
        }

        private String getActionName(int action) {
            switch (action) {
                case MotionEvent.ACTION_DOWN: return "DOWN";
                case MotionEvent.ACTION_MOVE: return "MOVE";
                case MotionEvent.ACTION_UP: return "UP";
                case MotionEvent.ACTION_CANCEL: return "CANCEL";
                default: return "OTHER(" + action + ")";
            }
        }
    }

    /**
     * 水平滑动的子View
     */
    private static class HorizontalScrollChild extends View {
        private static final String TAG = "ScrollConflictSolvedDemo.HorizontalScrollChild";

        private float mLastX;
        private float mLastY;
        private float mTranslateX = 0;
        private final float MAX_TRANSLATE = 200;

        public HorizontalScrollChild(Context context) {
            super(context);
            setBackgroundColor(Color.parseColor("#C8E6C9"));
        }

        @Override
        public boolean onTouchEvent(MotionEvent event) {
            float x = event.getX();
            float y = event.getY();

            ALog.dd(TAG, "onTouchEvent: action=" + getActionName(event.getAction()) +
                    ", x=" + x + ", y=" + y);

            switch (event.getAction()) {
                case MotionEvent.ACTION_DOWN:
                    mLastX = x;
                    mLastY = y;
                    ALog.dd(TAG, "✅ DOWN事件 - 记录初始位置: (" + mLastX + ", " + mLastY + ")");
                    ALog.dd(TAG, "🎉 子View正常接收DOWN事件，准备处理滑动");
                    return true;

                case MotionEvent.ACTION_MOVE:
                    float dx = x - mLastX;
                    float dy = y - mLastY;

                    ALog.dd(TAG, "🎉 MOVE事件 - 正常接收！dx=" + dx + ", dy=" + dy +
                            ", 当前translateX=" + mTranslateX);
                    ALog.dd(TAG, "✨ 没有收到ACTION_CANCEL，滑动冲突已解决！");

                    // 水平移动
                    float newTranslateX = mTranslateX + dx;
                    if (newTranslateX >= -MAX_TRANSLATE && newTranslateX <= MAX_TRANSLATE) {
                        mTranslateX = newTranslateX;
                        setTranslationX(mTranslateX);
                        ALog.dd(TAG, "🚀 水平移动成功，新位置: " + mTranslateX);
                    } else {
                        ALog.dd(TAG, "⚠️ 水平移动受限，保持在: " + mTranslateX);
                    }

                    mLastX = x;
                    mLastY = y;
                    break;

                case MotionEvent.ACTION_UP:
                    ALog.dd(TAG, "🎊 UP事件 - 滑动正常结束，最终位置: " + mTranslateX);
                    ALog.dd(TAG, "✅ 完美！整个滑动过程没有被打断");
                    break;

                case MotionEvent.ACTION_CANCEL:
                    ALog.dd(TAG, "❌ CANCEL事件 - 这不应该发生在解决方案中！");
                    ALog.dd(TAG, "如果看到这条日志，说明解决方案有问题");
                    break;
            }

            return true;
        }

        private String getActionName(int action) {
            switch (action) {
                case MotionEvent.ACTION_DOWN: return "DOWN";
                case MotionEvent.ACTION_MOVE: return "MOVE";
                case MotionEvent.ACTION_UP: return "UP";
                case MotionEvent.ACTION_CANCEL: return "CANCEL";
                default: return "OTHER(" + action + ")";
            }
        }
    }
}

