package com.example.demo;

import android.content.Context;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.TextView;
import androidx.annotation.NonNull;
import androidx.recyclerview.widget.RecyclerView;
import com.example.common.routers.Router;
import com.example.common.routers.RouterPath;
import java.lang.reflect.Field;
import java.util.ArrayList;
import java.util.List;

public class RoutesAdapter extends RecyclerView.Adapter<RoutesAdapter.RouteViewHolder> {

    private Context context;
    private List<RouteItem> routes;

    public RoutesAdapter(Context context) {
        this.context = context;
        this.routes = getAllRoutes();
    }

    @NonNull
    @Override
    public RouteViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {
        View view = LayoutInflater.from(context).inflate(R.layout.item_route, parent, false);
        return new RouteViewHolder(view);
    }

    @Override
    public void onBindViewHolder(@NonNull RouteViewHolder holder, int position) {
        RouteItem route = routes.get(position);
        holder.tvRouteName.setText(route.name + "\n" + route.path);

        holder.tvRouteName.setOnClickListener(v -> {
            // 点击跳转
            Router.switchFragment(route.path);
        });
    }

    @Override
    public int getItemCount() {
        return routes.size();
    }

    /**
     * 通过反射获取RouterPath中的所有路由
     */
    private List<RouteItem> getAllRoutes() {
        List<RouteItem> routeList = new ArrayList<>();

        try {
            Field[] fields = RouterPath.class.getDeclaredFields();
            for (Field field : fields) {
                if (field.getType() == String.class && field.getName().startsWith("VIEW_") 
                    || field.getName().startsWith("TEXTVIEW_")
                    || field.getName().startsWith("STUDY_")
                    || field.getName().startsWith("TEST_")) {
                    String path = (String) field.get(null);
                    String name = field.getName().replace("_", " ");
                    routeList.add(new RouteItem(name, path));
                }
            }
        } catch (Exception e) {
            e.printStackTrace();
            // 如果反射失败，添加一些默认路由
            routeList.add(new RouteItem("VIEW STUDY MAIN", RouterPath.VIEW_STUDY_MAIN));
            routeList.add(new RouteItem("TEXTVIEW STUDY MAIN", RouterPath.TEXTVIEW_STUDY_MAIN));
            routeList.add(new RouteItem("VIEW BASIC MEASURE", RouterPath.VIEW_BASIC_MEASURE));
            routeList.add(new RouteItem("VIEW EVENT DISPATCH", RouterPath.VIEW_EVENT_DISPATCH));
            routeList.add(new RouteItem("TEXTVIEW MEASURE", RouterPath.TEXTVIEW_MEASURE));
            routeList.add(new RouteItem("TEXTVIEW PERFORMANCE", RouterPath.TEXTVIEW_PERFORMANCE));
        }

        return routeList;
    }

    static class RouteViewHolder extends RecyclerView.ViewHolder {
        TextView tvRouteName;

        public RouteViewHolder(@NonNull View itemView) {
            super(itemView);
            tvRouteName = itemView.findViewById(R.id.tv_route_name);
        }
    }

    /**
     * 路由项数据类
     */
    static class RouteItem {
        String name;
        String path;

        public RouteItem(String name, String path) {
            this.name = name;
            this.path = path;
        }
    }
}

