 ```package com.example.demo.view;

import android.content.Context;
import android.graphics.Canvas;
import android.graphics.Color;
import android.graphics.Paint;
import android.util.AttributeSet;
import android.view.View;

import com.example.common.log.ALog;

/**
 * 演示View生命周期回调的自定义View
 */
public class ViewLifecycleDemo extends View {
    private static final String TAG = "ViewLifecycleDemo";

    private Paint mPaint;

    public ViewLifecycleDemo(Context context) {
        this(context, null);
    }

    public ViewLifecycleDemo(Context context, AttributeSet attrs) {
        this(context, attrs, 0);
    }

    public ViewLifecycleDemo(Context context, AttributeSet attrs, int defStyleAttr) {
        super(context, attrs, defStyleAttr);
        ALog.d(TAG, "构造方法被调用");
        initPaint();
    }

    private void initPaint() {
        mPaint = new Paint(Paint.ANTI_ALIAS_FLAG);
        mPaint.setTextSize(24);
        mPaint.setColor(Color.BLACK);
    }

    @Override
    protected void onAttachedToWindow() {
        super.onAttachedToWindow();
        ALog.d(TAG, "onAttachedToWindow - View被附加到窗口");
    }

    @Override
    protected void onDetachedFromWindow() {
        super.onDetachedFromWindow();
        ALog.d(TAG, "onDetachedFromWindow - View从窗口分离");
    }

    @Override
    protected void onVisibilityChanged(View changedView, int visibility) {
        super.onVisibilityChanged(changedView, visibility);
        String visibilityStr = getVisibilityString(visibility);
        ALog.d(TAG, "onVisibilityChanged - 可见性变化: " + visibilityStr);
    }

    @Override
    protected void onWindowVisibilityChanged(int visibility) {
        super.onWindowVisibilityChanged(visibility);
        String visibilityStr = getVisibilityString(visibility);
        ALog.d(TAG, "onWindowVisibilityChanged - 窗口可见性变化: " + visibilityStr);
    }

    @Override
    protected void onSizeChanged(int w, int h, int oldw, int oldh) {
        super.onSizeChanged(w, h, oldw, oldh);
        ALog.d(TAG, "onSizeChanged - 尺寸变化");
        ALog.d(TAG, "新尺寸: 宽=" + w + ", 高=" + h);
        ALog.d(TAG, "旧尺寸: 宽=" + oldw + ", 高=" + oldh);
    }

    @Override
    protected void onFinishInflate() {
        super.onFinishInflate();
        ALog.d(TAG, "onFinishInflate - 从XML加载完成");
    }

    @Override
    protected void onMeasure(int widthMeasureSpec, int heightMeasureSpec) {
        ALog.d(TAG, "onMeasure 被调用");

        // 设置一个固定大小
        int width = 300;
        int height = 100;

        setMeasuredDimension(width, height);
        ALog.d(TAG, "设置测量尺寸: 宽=" + width + ", 高=" + height);
    }

    @Override
    protected void onLayout(boolean changed, int left, int top, int right, int bottom) {
        super.onLayout(changed, left, top, right, bottom);
        ALog.d(TAG, "onLayout - 布局确定");
        ALog.d(TAG, "位置: left=" + left + ", top=" + top + ", right=" + right + ", bottom=" + bottom);
    }

    @Override
    protected void onDraw(Canvas canvas) {
        ALog.d(TAG, "onDraw - 开始绘制");

        // 绘制背景
        canvas.drawColor(Color.CYAN);

        // 绘制文本
        mPaint.setColor(Color.BLACK);
        canvas.drawText("View生命周期Demo", 10, 30, mPaint);
        canvas.drawText("查看日志了解回调流程", 10, 60, mPaint);

        ALog.d(TAG, "onDraw - 绘制完成");
    }

    @Override
    protected void onFocusChanged(boolean gainFocus, int direction, android.graphics.Rect previouslyFocusedRect) {
        super.onFocusChanged(gainFocus, direction, previouslyFocusedRect);
        ALog.d(TAG, "onFocusChanged - 焦点变化: " + (gainFocus ? "获得焦点" : "失去焦点"));
    }

    private String getVisibilityString(int visibility) {
        switch (visibility) {
            case View.VISIBLE:
                return "VISIBLE";
            case View.INVISIBLE:
                return "INVISIBLE";
            case View.GONE:
                return "GONE";
            default:
                return "UNKNOWN";
        }
    }
}
