package com.example.demo.view;

import android.content.Context;
import android.graphics.Canvas;
import android.graphics.Color;
import android.graphics.Paint;
import android.util.AttributeSet;
import android.view.View;
import android.view.ViewGroup;

import com.example.common.log.ALog;

/**
 * 高级测量演示：展示复杂的测量场景和父子View的测量关系
 */
public class AdvancedMeasureDemo extends ViewGroup {
    private static final String TAG = "AdvancedMeasureDemo";

    private Paint mPaint;
    private int mMeasureRound = 0;

    public AdvancedMeasureDemo(Context context) {
        this(context, null);
    }

    public AdvancedMeasureDemo(Context context, AttributeSet attrs) {
        this(context, attrs, 0);
    }

    public AdvancedMeasureDemo(Context context, AttributeSet attrs, int defStyleAttr) {
        super(context, attrs, defStyleAttr);
        ALog.d(TAG, "AdvancedMeasureDemo 构造方法");
        initPaint();
        setWillNotDraw(false);

        // 添加几个不同类型的子View
        addTestChildren();
    }

    private void initPaint() {
        mPaint = new Paint(Paint.ANTI_ALIAS_FLAG);
        mPaint.setTextSize(20);
        mPaint.setColor(Color.BLACK);
    }

    private void addTestChildren() {
        // 添加一个固定大小的子View
        View fixedChild = new View(getContext()) {
            @Override
            protected void onMeasure(int widthMeasureSpec, int heightMeasureSpec) {
                ALog.d(TAG, "固定大小子View onMeasure");
                setMeasuredDimension(100, 50);
            }
        };
        fixedChild.setBackgroundColor(Color.RED);
        addView(fixedChild);

        // 添加一个需要包裹内容的子View
        View wrapChild = new View(getContext()) {
            @Override
            protected void onMeasure(int widthMeasureSpec, int heightMeasureSpec) {
                ALog.d(TAG, "包裹内容子View onMeasure");
                ALog.d(TAG, "父View给的约束 - 宽:" + getModeString(MeasureSpec.getMode(widthMeasureSpec)) 
                       + " " + MeasureSpec.getSize(widthMeasureSpec));
                ALog.d(TAG, "父View给的约束 - 高:" + getModeString(MeasureSpec.getMode(heightMeasureSpec)) 
                       + " " + MeasureSpec.getSize(heightMeasureSpec));

                // 根据父View的约束计算自己的尺寸
                int width = Math.min(150, MeasureSpec.getSize(widthMeasureSpec));
                int height = Math.min(80, MeasureSpec.getSize(heightMeasureSpec));
                setMeasuredDimension(width, height);
            }
        };
        wrapChild.setBackgroundColor(Color.GREEN);
        addView(wrapChild);

        // 添加一个需要填充父View的子View
        View fillChild = new View(getContext()) {
            @Override
            protected void onMeasure(int widthMeasureSpec, int heightMeasureSpec) {
                ALog.d(TAG, "填充父View子View onMeasure");

                int width = MeasureSpec.getSize(widthMeasureSpec);
                int height = 60; // 固定高度

                ALog.d(TAG, "填充子View计算尺寸: " + width + " x " + height);
                setMeasuredDimension(width, height);
            }
        };
        fillChild.setBackgroundColor(Color.BLUE);
        addView(fillChild);
    }

    @Override
    protected void onMeasure(int widthMeasureSpec, int heightMeasureSpec) {
        mMeasureRound++;
        ALog.d(TAG, "========== 第" + mMeasureRound + "轮测量开始 ==========");

        // 解析父View给的约束
        int widthMode = MeasureSpec.getMode(widthMeasureSpec);
        int widthSize = MeasureSpec.getSize(widthMeasureSpec);
        int heightMode = MeasureSpec.getMode(heightMeasureSpec);
        int heightSize = MeasureSpec.getSize(heightMeasureSpec);

        ALog.d(TAG, "父View约束 - 宽:" + getModeString(widthMode) + " " + widthSize);
        ALog.d(TAG, "父View约束 - 高:" + getModeString(heightMode) + " " + heightSize);

        int totalHeight = 0;
        int maxWidth = 0;

        // 逐个测量子View
        for (int i = 0; i < getChildCount(); i++) {
            View child = getChildAt(i);
            ALog.d(TAG, "--- 开始测量第" + i + "个子View ---");

            // 创建子View的测量约束
            int childWidthSpec = MeasureSpec.makeMeasureSpec(widthSize, widthMode);
            int childHeightSpec = MeasureSpec.makeMeasureSpec(0, MeasureSpec.UNSPECIFIED);

            ALog.d(TAG, "给子View的约束 - 宽:" + getModeString(MeasureSpec.getMode(childWidthSpec)) 
                   + " " + MeasureSpec.getSize(childWidthSpec));
            ALog.d(TAG, "给子View的约束 - 高:" + getModeString(MeasureSpec.getMode(childHeightSpec)) 
                   + " " + MeasureSpec.getSize(childHeightSpec));

            // 测量子View
            child.measure(childWidthSpec, childHeightSpec);

            int childWidth = child.getMeasuredWidth();
            int childHeight = child.getMeasuredHeight();

            ALog.d(TAG, "第" + i + "个子View测量结果: " + childWidth + " x " + childHeight);

            totalHeight += childHeight;
            maxWidth = Math.max(maxWidth, childWidth);

            ALog.d(TAG, "--- 第" + i + "个子View测量完成 ---");
        }

        // 计算ViewGroup自身的尺寸
        int finalWidth = resolveSize(maxWidth, widthMeasureSpec);
        int finalHeight = resolveSize(totalHeight, heightMeasureSpec);

        ALog.d(TAG, "ViewGroup最终尺寸: " + finalWidth + " x " + finalHeight);
        ALog.d(TAG, "累计子View高度: " + totalHeight + ", 最大子View宽度: " + maxWidth);

        setMeasuredDimension(finalWidth, finalHeight);

        ALog.d(TAG, "========== 第" + mMeasureRound + "轮测量结束 ==========");
    }

    @Override
    protected void onLayout(boolean changed, int left, int top, int right, int bottom) {
        ALog.d(TAG, "========== onLayout 开始 ==========");
        ALog.d(TAG, "changed: " + changed);
        ALog.d(TAG, "ViewGroup位置: (" + left + ", " + top + ", " + right + ", " + bottom + ")");

        int currentTop = 0;

        for (int i = 0; i < getChildCount(); i++) {
            View child = getChildAt(i);
            int childWidth = child.getMeasuredWidth();
            int childHeight = child.getMeasuredHeight();

            // 水平居中，垂直依次排列
            int childLeft = (getWidth() - childWidth) / 2;
            int childTop = currentTop;
            int childRight = childLeft + childWidth;
            int childBottom = childTop + childHeight;

            ALog.d(TAG, "第" + i + "个子View布局: (" + childLeft + ", " + childTop + ", " 
                   + childRight + ", " + childBottom + ")");

            child.layout(childLeft, childTop, childRight, childBottom);

            currentTop += childHeight;
        }

        ALog.d(TAG, "========== onLayout 结束 ==========");
    }

    @Override
    protected void onDraw(Canvas canvas) {
        ALog.d(TAG, "onDraw 开始");

        // 绘制背景
        canvas.drawColor(Color.LTGRAY);

        // 绘制标题
        mPaint.setColor(Color.BLACK);
        mPaint.setTextSize(24);
        canvas.drawText("高级测量演示", 10, 30, mPaint);

        mPaint.setTextSize(16);
        canvas.drawText("查看日志了解测量流程", 10, 60, mPaint);
        canvas.drawText("测量轮数: " + mMeasureRound, 10, 80, mPaint);

        ALog.d(TAG, "onDraw 结束");
    }

    private String getModeString(int mode) {
        switch (mode) {
            case MeasureSpec.EXACTLY:
                return "EXACTLY";
            case MeasureSpec.AT_MOST:
                return "AT_MOST";
            case MeasureSpec.UNSPECIFIED:
                return "UNSPECIFIED";
            default:
                return "UNKNOWN";
        }
    }

    public void resetMeasureCount() {
        mMeasureRound = 0;
        ALog.d(TAG, "测量轮数已重置");
    }
}

