package com.example.demo.view;

import android.graphics.Color;
import android.view.View;
import android.widget.Button;
import android.widget.LinearLayout;
import android.widget.TextView;

import com.drake.tooltip.ToastKt;
import com.example.common.base.BaseFragment;
import com.example.common.log.ALog;
import com.example.common.routers.RouterPath;
import com.example.demo.R;
import com.example.demo.databinding.FragmentViewBinding;
import com.therouter.router.Route;

@Route(path = RouterPath.VIEW, description = "自定义View页面")
public class ViewFragment extends BaseFragment<FragmentViewBinding> {
    private static final String TAG = "ViewFragment";

    private CustomDemoView mCustomDemoView;
    private TouchEventDemoViewGroup mTouchEventViewGroup;
    private ViewLifecycleDemo mViewLifecycleDemo;
    private AdvancedMeasureDemo mAdvancedMeasureDemo;
    private NestedTouchEventDemo mNestedTouchEventDemo;

    public ViewFragment() {
        super(R.layout.fragment_view);
    }

    @Override
    protected void initView() {
        super.initView();

        ALog.d(TAG, "ViewFragment initView开始");

        // 创建标题
        TextView titleView = new TextView(getContext());
        titleView.setText("View 测量布局绘制 & 触摸事件分发 Demo");
        titleView.setTextSize(18);
        titleView.setTextColor(Color.BLACK);
        titleView.setPadding(20, 20, 20, 10);
        binding.main.addView(titleView);

        // 添加说明文本
        TextView descView = new TextView(getContext());
        descView.setText("观察日志输出了解View的工作流程\n触摸下方区域查看效果");
        descView.setTextSize(14);
        descView.setTextColor(Color.GRAY);
        descView.setPadding(20, 0, 20, 20);
        binding.main.addView(descView);

        // 创建自定义View演示测量布局绘制
        mCustomDemoView = new CustomDemoView(getContext());
        mCustomDemoView.setPadding(20, 20, 20, 20);
        LinearLayout.LayoutParams customViewParams = new LinearLayout.LayoutParams(
            LinearLayout.LayoutParams.MATCH_PARENT,
            LinearLayout.LayoutParams.WRAP_CONTENT
        );
        customViewParams.setMargins(20, 10, 20, 10);
        binding.main.addView(mCustomDemoView, customViewParams);

        // 添加重置按钮
        Button resetButton = new Button(getContext());
        resetButton.setText("重置计数器");
        resetButton.setOnClickListener(v -> {
            ALog.d(TAG, "重置按钮被点击");
            mCustomDemoView.resetCounters();
            // 触发重新测量布局绘制
            mCustomDemoView.requestLayout();
        });
        LinearLayout.LayoutParams buttonParams = new LinearLayout.LayoutParams(
            LinearLayout.LayoutParams.WRAP_CONTENT,
            LinearLayout.LayoutParams.WRAP_CONTENT
        );
        buttonParams.setMargins(20, 10, 20, 10);
        binding.main.addView(resetButton, buttonParams);

        // 添加分隔线
        View divider = new View(getContext());
        divider.setBackgroundColor(Color.LTGRAY);
        LinearLayout.LayoutParams dividerParams = new LinearLayout.LayoutParams(
            LinearLayout.LayoutParams.MATCH_PARENT, 2
        );
        dividerParams.setMargins(20, 20, 20, 20);
        binding.main.addView(divider, dividerParams);

        // 创建触摸事件分发演示
        TextView touchTitleView = new TextView(getContext());
        touchTitleView.setText("触摸事件分发演示");
        touchTitleView.setTextSize(16);
        touchTitleView.setTextColor(Color.BLACK);
        touchTitleView.setPadding(20, 10, 20, 10);
        binding.main.addView(touchTitleView);

        mTouchEventViewGroup = new TouchEventDemoViewGroup(getContext());
        LinearLayout.LayoutParams touchViewParams = new LinearLayout.LayoutParams(
            LinearLayout.LayoutParams.MATCH_PARENT, 400
        );
        touchViewParams.setMargins(20, 10, 20, 10);
        binding.main.addView(mTouchEventViewGroup, touchViewParams);

        // 添加拦截控制按钮
        Button interceptButton = new Button(getContext());
        interceptButton.setText("切换事件拦截");
        interceptButton.setOnClickListener(v -> {
            boolean currentState = mTouchEventViewGroup.isInterceptTouchEvent();
            mTouchEventViewGroup.setInterceptTouchEvent(!currentState);
            ALog.d(TAG, "拦截状态切换为: " + !currentState);
            ToastKt.toast("事件拦截已" + (currentState ? "关闭" : "开启"));
        });
        LinearLayout.LayoutParams interceptButtonParams = new LinearLayout.LayoutParams(
            LinearLayout.LayoutParams.WRAP_CONTENT,
            LinearLayout.LayoutParams.WRAP_CONTENT
        );
        interceptButtonParams.setMargins(20, 10, 20, 10);
        binding.main.addView(interceptButton, interceptButtonParams);

        // 添加第二个分隔线
        View divider2 = new View(getContext());
        divider2.setBackgroundColor(Color.LTGRAY);
        LinearLayout.LayoutParams dividerParams2 = new LinearLayout.LayoutParams(
            LinearLayout.LayoutParams.MATCH_PARENT, 2
        );
        dividerParams2.setMargins(20, 20, 20, 20);
        binding.main.addView(divider2, dividerParams2);

        // 添加生命周期演示
        TextView lifecycleTitleView = new TextView(getContext());
        lifecycleTitleView.setText("View生命周期演示");
        lifecycleTitleView.setTextSize(16);
        lifecycleTitleView.setTextColor(Color.BLACK);
        lifecycleTitleView.setPadding(20, 10, 20, 10);
        binding.main.addView(lifecycleTitleView);

        mViewLifecycleDemo = new ViewLifecycleDemo(getContext());
        LinearLayout.LayoutParams lifecycleParams = new LinearLayout.LayoutParams(
            LinearLayout.LayoutParams.MATCH_PARENT,
            LinearLayout.LayoutParams.WRAP_CONTENT
        );
        lifecycleParams.setMargins(20, 10, 20, 10);
        binding.main.addView(mViewLifecycleDemo, lifecycleParams);

        // 添加可见性控制按钮
        Button visibilityButton = new Button(getContext());
        visibilityButton.setText("切换可见性");
        visibilityButton.setOnClickListener(v -> {
            if (mViewLifecycleDemo.getVisibility() == View.VISIBLE) {
                mViewLifecycleDemo.setVisibility(View.INVISIBLE);
                ALog.d(TAG, "设置ViewLifecycleDemo为INVISIBLE");
            } else {
                mViewLifecycleDemo.setVisibility(View.VISIBLE);
                ALog.d(TAG, "设置ViewLifecycleDemo为VISIBLE");
            }
        });
        LinearLayout.LayoutParams visibilityButtonParams = new LinearLayout.LayoutParams(
            LinearLayout.LayoutParams.WRAP_CONTENT,
            LinearLayout.LayoutParams.WRAP_CONTENT
        );
        visibilityButtonParams.setMargins(20, 10, 20, 10);
        binding.main.addView(visibilityButton, visibilityButtonParams);

        // 添加第三个分隔线
        View divider3 = new View(getContext());
        divider3.setBackgroundColor(Color.LTGRAY);
        LinearLayout.LayoutParams dividerParams3 = new LinearLayout.LayoutParams(
            LinearLayout.LayoutParams.MATCH_PARENT, 2
        );
        dividerParams3.setMargins(20, 20, 20, 20);
        binding.main.addView(divider3, dividerParams3);

        // 添加高级测量演示
        TextView advancedTitleView = new TextView(getContext());
        advancedTitleView.setText("高级测量演示 - 父子View测量关系");
        advancedTitleView.setTextSize(16);
        advancedTitleView.setTextColor(Color.BLACK);
        advancedTitleView.setPadding(20, 10, 20, 10);
        binding.main.addView(advancedTitleView);

        mAdvancedMeasureDemo = new AdvancedMeasureDemo(getContext());
        LinearLayout.LayoutParams advancedParams = new LinearLayout.LayoutParams(
            LinearLayout.LayoutParams.MATCH_PARENT,
            LinearLayout.LayoutParams.WRAP_CONTENT
        );
        advancedParams.setMargins(20, 10, 20, 10);
        binding.main.addView(mAdvancedMeasureDemo, advancedParams);

        // 添加重新测量按钮
        Button remeasureButton = new Button(getContext());
        remeasureButton.setText("触发重新测量");
        remeasureButton.setOnClickListener(v -> {
            ALog.d(TAG, "触发重新测量按钮被点击");
            mAdvancedMeasureDemo.resetMeasureCount();
            mAdvancedMeasureDemo.requestLayout();
            ALog.d(TAG, "已调用requestLayout()触发重新测量");
        });
        LinearLayout.LayoutParams remeasureButtonParams = new LinearLayout.LayoutParams(
            LinearLayout.LayoutParams.WRAP_CONTENT,
            LinearLayout.LayoutParams.WRAP_CONTENT
        );
        remeasureButtonParams.setMargins(20, 10, 20, 20);
        binding.main.addView(remeasureButton, remeasureButtonParams);

        ALog.d(TAG, "ViewFragment initView完成");
    }

    @Override
    protected void initData() {
        super.initData();
        ALog.d(TAG, "ViewFragment initData");
    }

    @Override
    public void onResume() {
        super.onResume();
        ALog.d(TAG, "ViewFragment onResume - 触发View重新测量布局绘制");
    }
}

