        super.initView();

        ALog.d(TAG, "ViewEventDispatchFragment initView开始");

        // 创建标题
        TextView titleView = new TextView(getContext());
        titleView.setText("触摸事件分发机制演示");
        titleView.setTextSize(20);
        titleView.setTextColor(Color.BLACK);
        titleView.setPadding(20, 20, 20, 10);
        binding.main.addView(titleView);

        // 添加说明文本
        TextView descView = new TextView(getContext());
        descView.setText("学习要点：\n" +
                "• dispatchTouchEvent(): 事件分发的入口\n" +
                "• onInterceptTouchEvent(): ViewGroup如何拦截事件\n" +
                "• onTouchEvent(): 事件的最终处理\n" +
                "• 事件传递流程：dispatch -> intercept -> 子View -> onTouchEvent\n\n" +
                "操作：点击黄色子View区域，观察完整的事件分发流程");
        descView.setTextSize(14);
        descView.setTextColor(Color.GRAY);
        descView.setPadding(20, 0, 20, 20);
        binding.main.addView(descView);

        // 创建触摸事件分发演示
        mTouchEventViewGroup = new TouchEventDemoViewGroup(getContext());
        LinearLayout.LayoutParams touchViewParams = new LinearLayout.LayoutParams(
            LinearLayout.LayoutParams.MATCH_PARENT, 400
        );
        touchViewParams.setMargins(20, 10, 20, 10);
        binding.main.addView(mTouchEventViewGroup, touchViewParams);

        // 添加拦截控制按钮
        Button interceptButton = new Button(getContext());
        interceptButton.setText("切换事件拦截模式");
        interceptButton.setOnClickListener(v -> {
            boolean currentState = mTouchEventViewGroup.isInterceptTouchEvent();
            mTouchEventViewGroup.setInterceptTouchEvent(!currentState);
            ALog.d(TAG, "拦截状态切换为: " + !currentState);
            ToastKt.toast("事件拦截已" + (currentState ? "关闭" : "开启"));
        });
        LinearLayout.LayoutParams interceptButtonParams = new LinearLayout.LayoutParams(
            LinearLayout.LayoutParams.WRAP_CONTENT,
            LinearLayout.LayoutParams.WRAP_CONTENT
        );
        interceptButtonParams.setMargins(20, 10, 20, 10);
        binding.main.addView(interceptButton, interceptButtonParams);

        // 添加事件流程说明
        TextView flowView = new TextView(getContext());
        flowView.setText("🔄 事件流程：\n" +
                "1. 父ViewGroup.dispatchTouchEvent()\n" +
                "2. 父ViewGroup.onInterceptTouchEvent()\n" +
                "3. 子View.dispatchTouchEvent() (如果不拦截)\n" +
                "4. 子View.onTouchEvent() (如果不拦截)\n" +
                "5. 父ViewGroup.onTouchEvent() (如果子View不处理)");
        flowView.setTextSize(13);
        flowView.setTextColor(Color.DKGRAY);
        flowView.setPadding(20, 10, 20, 10);
        flowView.setBackgroundColor(Color.LTGRAY);
        LinearLayout.LayoutParams flowParams = new LinearLayout.LayoutParams(
            LinearLayout.LayoutParams.MATCH_PARENT,
            LinearLayout.LayoutParams.WRAP_CONTENT
        );
        flowParams.setMargins(20, 20, 20, 10);
        binding.main.addView(flowView, flowParams);

        // 添加日志提示
        TextView logTip = new TextView(getContext());
        logTip.setText("💡 调试技巧：使用 adb logcat | grep \"TouchEventViewGroup\" 查看详细日志");
        logTip.setTextSize(12);
        logTip.setTextColor(Color.BLUE);
        logTip.setPadding(20, 10, 20, 10);
        binding.main.addView(logTip);

        ALog.d(TAG, "ViewEventDispatchFragment initView完成");
    }

    @Override
    protected void initData() {
        super.initData();
        ALog.d(TAG, "ViewEventDispatchFragment initData");
    }

    @Override
    public void onResume() {
        super.onResume();
        ALog.d(TAG, "ViewEventDispatchFragment onResume");
    }
}

