package com.example.demo.view;

import android.content.Context;
import android.graphics.Canvas;
import android.graphics.Color;
import android.graphics.Paint;
import android.util.AttributeSet;
import android.view.MotionEvent;
import android.view.View;
import android.view.ViewGroup;

import com.example.common.log.ALog;

/**
 * 演示触摸事件分发流程的自定义ViewGroup
 */
public class TouchEventDemoViewGroup extends ViewGroup {
    private static final String TAG = "TouchEventViewGroup";

    private Paint mPaint;
    private boolean mInterceptTouchEvent = false;
    private int mChildCount = 0;

    public TouchEventDemoViewGroup(Context context) {
        this(context, null);
    }

    public TouchEventDemoViewGroup(Context context, AttributeSet attrs) {
        this(context, attrs, 0);
    }

    public TouchEventDemoViewGroup(Context context, AttributeSet attrs, int defStyleAttr) {
        super(context, attrs, defStyleAttr);
        ALog.dd(TAG, "TouchEventDemoViewGroup 构造方法被调用");
        initPaint();
        setWillNotDraw(false); // 允许ViewGroup绘制

        // 添加一个子View来演示事件分发
        addChildView();
    }

    private void initPaint() {
        mPaint = new Paint(Paint.ANTI_ALIAS_FLAG);
        mPaint.setTextSize(32);
        mPaint.setColor(Color.BLACK);
    }

    private void addChildView() {
        // 创建一个简单的子View
        View childView = new View(getContext()) {
            @Override
            public boolean onTouchEvent(MotionEvent event) {
                ALog.dd(TAG, "子View onTouchEvent: " + TouchEventDemoViewGroup.this.getActionString(event.getAction()));
                ALog.dd(TAG, "子View 坐标: x=" + event.getX() + ", y=" + event.getY());
                // 返回true表示消费事件
                return true;
            }
        };

        childView.setBackgroundColor(Color.YELLOW);
        LayoutParams params = new LayoutParams(300, 200);
        addView(childView, params);
    }

    @Override
    protected void onMeasure(int widthMeasureSpec, int heightMeasureSpec) {
        ALog.dd(TAG, "onMeasure - 开始测量ViewGroup");

        // 测量所有子View
        for (int i = 0; i < getChildCount(); i++) {
            View child = getChildAt(i);
            measureChild(child, widthMeasureSpec, heightMeasureSpec);
            ALog.dd(TAG, "测量第" + i + "个子View: 宽=" + child.getMeasuredWidth() + ", 高=" + child.getMeasuredHeight());
        }

        // 设置ViewGroup自身的测量尺寸
        int width = MeasureSpec.getSize(widthMeasureSpec);
        int height = MeasureSpec.getSize(heightMeasureSpec);
        setMeasuredDimension(width, height);

        ALog.dd(TAG, "ViewGroup测量完成: 宽=" + width + ", 高=" + height);
    }

    @Override
    protected void onLayout(boolean changed, int left, int top, int right, int bottom) {
        ALog.dd(TAG, "onLayout - 开始布局子View");
        ALog.dd(TAG, "ViewGroup位置: left=" + left + ", top=" + top + ", right=" + right + ", bottom=" + bottom);

        // 简单布局：将子View放在ViewGroup中央
        for (int i = 0; i < getChildCount(); i++) {
            View child = getChildAt(i);
            int childWidth = child.getMeasuredWidth();
            int childHeight = child.getMeasuredHeight();

            int childLeft = (getWidth() - childWidth) / 2;
            int childTop = (getHeight() - childHeight) / 2 + i * 50; // 稍微错开
            int childRight = childLeft + childWidth;
            int childBottom = childTop + childHeight;

            child.layout(childLeft, childTop, childRight, childBottom);

            ALog.dd(TAG, "第" + i + "个子View布局: left=" + childLeft + ", top=" + childTop +
                   ", right=" + childRight + ", bottom=" + childBottom);
        }

        ALog.dd(TAG, "onLayout 完成");
    }

    @Override
    protected void onDraw(Canvas canvas) {
        super.onDraw(canvas);

        ALog.dd(TAG, "ViewGroup onDraw");

        // 绘制背景
        canvas.drawColor(Color.LTGRAY);

        // 绘制ViewGroup信息
        mPaint.setColor(Color.BLACK);
        canvas.drawText("TouchEventViewGroup", 20, 50, mPaint);

        String interceptInfo = "拦截模式: " + (mInterceptTouchEvent ? "开启" : "关闭");
        canvas.drawText(interceptInfo, 20, 90, mPaint);

        canvas.drawText("点击切换拦截模式", 20, 130, mPaint);
        canvas.drawText("黄色区域是子View", 20, 170, mPaint);
    }

    @Override
    public boolean dispatchTouchEvent(MotionEvent ev) {
        ALog.dd(TAG, "========== dispatchTouchEvent ==========");
        ALog.dd(TAG, "事件类型: " + getActionString(ev.getAction()));
        ALog.dd(TAG, "坐标: x=" + ev.getX() + ", y=" + ev.getY());

        boolean result = super.dispatchTouchEvent(ev);

        ALog.dd(TAG, "dispatchTouchEvent 返回: " + result);
        ALog.dd(TAG, "========== dispatchTouchEvent 结束 ==========");

        return result;
    }

    @Override
    public boolean onInterceptTouchEvent(MotionEvent ev) {
        ALog.dd(TAG, "========== onInterceptTouchEvent ==========");
        ALog.dd(TAG, "事件类型: " + getActionString(ev.getAction()));
        ALog.dd(TAG, "当前拦截设置: " + mInterceptTouchEvent);

        // 根据设置决定是否拦截
        boolean intercept = mInterceptTouchEvent;

        ALog.dd(TAG, "onInterceptTouchEvent 返回: " + intercept);
        ALog.dd(TAG, "========== onInterceptTouchEvent 结束 ==========");

        return intercept;
    }

    @Override
    public boolean onTouchEvent(MotionEvent event) {
        ALog.dd(TAG, "========== ViewGroup onTouchEvent ==========");
